(1, 'contact-form', 'Contact Us', 'We would like to hear from you. Please send us a message by filling out the form below and we will get back with you shortly.', 0, 0, 0, 0, 0, 'published', 'a:18:{s:12:\"custom_style\";i:1;s:12:\"submit_value\";s:6:\"Submit\";s:14:\"success_action\";s:7:\"message\";s:11:\"success_msg\";s:54:\"Your responses were successfully submitted. Thank you!\";s:9:\"show_form\";i:0;s:7:\"akismet\";s:0:\"\";s:8:\"honeypot\";s:5:\"basic\";s:8:\"antispam\";i:0;s:7:\"no_save\";i:0;s:9:\"ajax_load\";i:0;s:11:\"js_validate\";i:0;s:10:\"form_class\";s:0:\"\";s:11:\"before_html\";s:224:\"<legend class=\"frm_screen_reader\">[form_name]</legend>\n[if form_name]<h3 class=\"frm_form_title\">[form_name]</h3>[/if form_name]\n[if form_description]<div class=\"frm_description\">[form_description]</div>[/if form_description]\";s:10:\"after_html\";s:0:\"\";s:11:\"submit_html\";s:514:\"<div class=\"frm_submit\">\n[if back_button]<button type=\"submit\" name=\"frm_prev_page\" formnovalidate=\"formnovalidate\" class=\"frm_prev_page\" [back_hook]>[back_label]</button>[/if back_button]\n<button class=\"frm_button_submit\" type=\"submit\"  [button_action]>[button_label]</button>\n[if save_draft]<a href=\"#\" tabindex=\"0\" class=\"frm_save_draft\" [draft_hook]>[draft_label]</a>[/if save_draft]\n[if start_over]<a href=\"#\" tabindex=\"0\" class=\"frm_start_over\" [start_over_hook]>[start_over_label]</a>[/if start_over]\n</div>\";s:10:\"show_title\";i:0;s:16:\"show_description\";i:0;s:11:\"ajax_submit\";i:0;}', '2023-08-08 12:51:21');
