(258, 'frm_inbox_cache', 'a:3:{s:7:\"timeout\";i:1691531481;s:5:\"value\";s:2478:\"[{\"key\":\"videos\",\"subject\":\"Did you know?\",\"message\":\"There\'s a growing collection of great video tutorials ready for you! Have a request? Let us know! Don\'t forget to subscribe so you\'ll hear about new videos too.\",\"cta\":\"<a href=\\\"https:\\/\\/formidableforms.com\\/knowledgebase\\/latest-videos\\/?utm_source=WordPress&utm_medium=inbox&utm_campaign=liteplugin&utm_content=videos\\\" class=\\\"button-primary frm-button-primary\\\" target=\\\"_blank\\\" rel=\\\"noopener noreferrer\\\">Learn More<\\/a> <a href=\\\"#\\\" class=\\\"button-secondary frm-button-secondary frm_inbox_dismiss\\\">Dismiss<\\/a>\",\"icon\":\"frm_support_icon\",\"starts\":\"\",\"type\":\"news\",\"who\":[\"all\"]},{\"key\":\"stripe-payments\",\"subject\":\"&#91;New] Get paid easier with more Stripe payment options.\",\"message\":\"Good news! You can accept 15+ more payment options with Stripe payments. These include Cash App, ACH, and Klarna.\\r\",\"\\r\":null,\"Say goodbye to limited payments and hello to flexibility! (More flexibility = more revenue!) You already have access to these great new options. Why wait?\":null,\"cta\":\"<a href=\\\"https:\\/\\/formidableforms.com\\/stripe-payment-options-wordpress\\/?utm_source=WordPress&utm_medium=inbox&utm_campaign=liteplugin&utm_content=stripe-payments\\\" class=\\\"button-primary frm-button-primary\\\" target=\\\"_blank\\\" rel=\\\"noopener noreferrer\\\">Learn More<\\/a> <a href=\\\"#\\\" class=\\\"button-secondary frm-button-secondary frm_inbox_dismiss\\\">Dismiss<\\/a>\",\"icon\":\"frm_price_tags_icon\",\"starts\":1690848000,\"expires\":1694476800,\"type\":\"news\",\"who\":[\"business\",\"elite\"],\"banner\":\"And easier payments mean more payments. So turn it on today.\"},{\"key\":\"stripe-payments-lite\",\"subject\":\"&#91;New] Get paid easier with more Stripe payment options.\",\"message\":\"Good news! You can accept 15+ new payment options with Stripe payments. These include Cash App, ACH, and Klarna.\\r\",\"\\r\":null,\"Say goodbye to limited payments and hello to flexibility! (Hint: more flexibility = more revenue!)\":null,\"cta\":\"<a href=\\\"https:\\/\\/formidableforms.com\\/features\\/stripe-payments-for-wordpress\\/?utm_source=WordPress&utm_medium=inbox&utm_campaign=liteplugin&utm_content=stripe-payments-lite\\\" class=\\\"button-primary frm-button-primary\\\" target=\\\"_blank\\\" rel=\\\"noopener noreferrer\\\">Learn More<\\/a> <a href=\\\"#\\\" class=\\\"button-secondary frm-button-secondary frm_inbox_dismiss\\\">Dismiss<\\/a>\",\"icon\":\"frm_price_tags_icon\",\"starts\":1690848000,\"expires\":1694476800,\"type\":\"news\",\"who\":[\"free\",\"basic\",\"personal\",\"grandfathered\",\"expired\"]}]\";s:7:\"version\";s:5:\"6.4.1\";}', 'no'),
(259, 'frm_inbox', 'a:3:{s:6:\"videos\";a:10:{s:7:\"message\";s:160:\"There\'s a growing collection of great video tutorials ready for you! Have a request? Let us know! Don\'t forget to subscribe so you\'ll hear about new videos too.\";s:7:\"subject\";s:13:\"Did you know?\";s:4:\"icon\";s:16:\"frm_support_icon\";s:3:\"cta\";s:334:\"<a href=\"https://formidableforms.com/knowledgebase/latest-videos/?utm_source=WordPress&utm_medium=inbox&utm_campaign=liteplugin&utm_content=videos\" class=\"button-primary frm-button-primary\" target=\"_blank\" rel=\"noopener noreferrer\">Learn More</a> <a href=\"#\" class=\"button-secondary frm-button-secondary frm_inbox_dismiss\">Dismiss</a>\";s:7:\"expires\";b:0;s:3:\"who\";a:1:{i:0;s:3:\"all\";}s:4:\"type\";s:4:\"news\";s:3:\"key\";s:6:\"videos\";s:6:\"starts\";s:0:\"\";s:7:\"created\";i:1691499081;}s:15:\"stripe-payments\";a:13:{s:7:\"message\";s:114:\"Good news! You can accept 15+ more payment options with Stripe payments. These include Cash App, ACH, and Klarna.\r\";s:7:\"subject\";s:59:\"&#91;New] Get paid easier with more Stripe payment options.\";s:4:\"icon\";s:19:\"frm_price_tags_icon\";s:3:\"cta\";s:348:\"<a href=\"https://formidableforms.com/stripe-payment-options-wordpress/?utm_source=WordPress&utm_medium=inbox&utm_campaign=liteplugin&utm_content=stripe-payments\" class=\"button-primary frm-button-primary\" target=\"_blank\" rel=\"noopener noreferrer\">Learn More</a> <a href=\"#\" class=\"button-secondary frm-button-secondary frm_inbox_dismiss\">Dismiss</a>\";s:7:\"expires\";i:1694476800;s:3:\"who\";a:2:{i:0;s:8:\"business\";i:1;s:5:\"elite\";}s:4:\"type\";s:4:\"news\";s:3:\"key\";s:15:\"stripe-payments\";s:1:\"\r\";N;s:154:\"Say goodbye to limited payments and hello to flexibility! (More flexibility = more revenue!) You already have access to these great new options. Why wait?\";N;s:6:\"starts\";i:1690848000;s:6:\"banner\";s:60:\"And easier payments mean more payments. So turn it on today.\";s:7:\"created\";i:1691499081;}s