(274, 'frm_applications_l', 'a:3:{s:7:\"timeout\";i:1691531492;s:5:\"value\";s:8377:\"{\"28192313\":{\"name\":\"Invoice PDF Template\",\"slug\":\"invoice-pdf\",\"description\":\"Create informative and detailed invoices in a short amount of time. All you have to do is input the needed information and the rest will be done for you.\",\"id\":28192313,\"released\":\"2023-04-18 16:06:51\",\"categories\":[\"Business\"],\"link\":\"https:\\/\\/formidableforms.com\\/view-templates\\/invoice-pdf\\/\",\"icon\":[\"https:\\/\\/formidableforms.com\\/wp-content\\/uploads\\/formidable\\/233\\/invoice-pdf-thumbnail.png\",1500,750,false],\"key\":\"invoice-pdf\",\"forms\":\"1\",\"views\":\"1\",\"download_id\":\"\",\"min_plan\":\"Business\",\"is_new\":false},\"28183762\":{\"name\":\"Certificate Template\",\"slug\":\"certificate-template\",\"description\":\"Create professional and personalized certificates in just a few clicks. Simply input your information, customize to your liking, and generate a high-quality PDF certificate ready for printing or digital sharing.\",\"id\":28183762,\"released\":\"2023-03-14 08:06:00\",\"categories\":[\"Business\"],\"link\":\"https:\\/\\/formidableforms.com\\/view-templates\\/certificate-template\\/\",\"icon\":[\"https:\\/\\/formidableforms.com\\/wp-content\\/uploads\\/2023\\/03\\/certificate-template-sm.png\",799,400,false],\"key\":\"certificate\",\"forms\":\"1\",\"views\":\"2\",\"download_id\":\"\",\"min_plan\":\"Business\",\"is_new\":false},\"28067848\":{\"name\":\"Business Hours Template\",\"slug\":\"business-hours-template\",\"description\":\"Set your business hours and show a message to let your visitors know if you are currently open or closed.\",\"id\":28067848,\"released\":\"2021-07-05 16:05:50\",\"categories\":[\"Business\",\"Personal+Views\"],\"link\":\"https:\\/\\/formidableforms.com\\/view-templates\\/business-hours-template\\/\",\"icon\":[\"https:\\/\\/formidableforms.com\\/wp-content\\/uploads\\/edd\\/2018\\/06\\/show-business-hours-e1625